import "./FormInput.css";

const FormInput = (props) => {
   const { label, errorMessage, onChange, ...inputProps } = props;
   return (
      <div className="formInput">
         <label>{label}</label>
         <input {...inputProps} onChange={onChange} />
         <span>{errorMessage}</span>
      </div>
   )
}

export default FormInput;
