import { useState } from "react";
import { useNavigate } from 'react-router-dom';
import './App.css';
import FormInput from './components/FormInput';

function App() {

  const navigate = useNavigate();

  const [values, setValues] = useState({
    username: "",
    lastName: "",
    email: "",
    password: "",
  });

  const inputs = [{
    id: 1,
    name: "username",
    type: "text",
    placeholder: "Username",
    errorMessage: "Ім'я від 3 до 16 символів",
    label: "Username",
    pattern: "^[A-Za-z0-9]{3,16}$",
    required: true,
  },
  {
    id: 2,
    name: "lastName",
    type: "text",
    placeholder: "Last Name",
    errorMessage: "Прізвище від 3 до 16 символів",
    label: "Last Name",
    pattern: "^[A-Za-z0-9]{3,16}$",
    required: true,
  },
  {
    id: 3,
    name: "email",
    type: "email",
    placeholder: "Email",
    errorMessage: "Email – повинен містити: мінімум 3 символи, собачку, мінімум 2 символи, крапку та мінімум 2 символи",
    label: "Email",
    pattern: '^(([^<>()[\\].,;:\\s@"]+(\\.[^<>()[\\].,;:\\s@"]+)*)|(".+"))@(([^<>()[\\].,;:\\s@"]+\\.)+[^<>()[\\].,;:\\s@"]{2,})$',
    required: true,
  },
  {
    id: 4,
    name: "password",
    type: "password",
    placeholder: "Password",
    errorMessage: "Пароль повинен містити від 8 символів, включаючи 1 маленьку літеру(лат.) та 1 велику літеру(лат.)",
    label: "Password",
    pattern: "^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])[a-zA-Z0-9!@#$%^&*]{8,20}",
    required: true,
  }];

  const handleSubmit = (e) => {
    e.preventDefault();
    localStorage.setItem("user", values.JSON.stringify());
    // navigate("/login")
  }

  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  }
  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <h1>Register</h1>
        {inputs.map((input) => (
          <FormInput key={input.id} {...input} value={values[input.name]} onChange={onChange} />
        ))}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
