import { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import "./signIn.css";
import FormSignIn from '../../components/FormSignIn';
import { dataSignIn } from '../../data/dataSignIn';

const SignIn = () => {

   // const navigate = useNavigate();
   const [errors, setErrors] = useState("");

   const [values, setValues] = useState({
      email: "",
      password: "",
   });

   const [rememberUser, setRememberUser] = useState(false);

   const users = JSON.parse(localStorage.getItem("users"));
   const savedUser = JSON.parse(localStorage.getItem("savedUser"));


   // const searchLoginUser = users.some(el => el.email === values.email && el.password === values.password);



   const handleSubmit = (e) => {
      e.preventDefault();
      const login = users.some(el => el.email === values.email);
      const password = users.some(el => el.password === values.password);
      if (!login) setErrors("User does not exist.");
      else if (!password) setErrors("Incorect password.");
      else if (login && password && rememberUser) localStorage.setItem("savedUser", JSON.stringify(values));
   }

   // useEffect(() => { console.log(errors); }, [errors])

   useEffect(() => {
      if (savedUser) {
         setValues(savedUser);
      }
   }, []);


   const onChange = (e) => {
      setValues({ ...values, [e.target.name]: e.target.value });
   }
   return (
      <div className="signUp">
         <form onSubmit={handleSubmit}>
            <h1>Sign In</h1>
            {dataSignIn.map((input) => (
               <FormSignIn key={input.id} {...input} value={values[input.name]} onChange={onChange} />
            ))}
            <div className='blockCheck'>
               <input type="checkbox" onChange={() => setRememberUser(!rememberUser)} defaultChecked={rememberUser} />
               <p className='rememberMe'>Запам'ятати мене</p>
            </div>
            <button type="submit">Submit</button>
            <p>Немає облікового запису? Зареєструватись</p>
         </form>
      </div>
   );
}

export default SignIn;
